var lamCart={totalShipping:0,totalItems:0,totalOrder:0,shippingBase:0,initialize:function(e){this.shippingBase=parseFloat(e);this.totalShipping=this.shippingBase},addItem:function(e){if(typeof e.attr("data-relate")=="undefined")itemLine=e.attr("data-product");else{optionval=$("#"+e.attr("data-relate")).val();itemLine=e.attr("data-product")+" - "+optionval}cost=e.attr("data-cost");shipping=e.attr("data-shipping");this.addItemsTotal(cost);this.addShippingTotal(shipping);this.addOrderTotal();itemLineDOMtext=itemLine.replace(/ /g,"");itemLineDOMtext=itemLineDOMtext.replace(/-/g,"");itemLineDOM=$("#"+itemLineDOMtext);itemLineDOM.get(0)?$("#"+itemLineDOMtext+"Quantity").val(parseInt($("#"+itemLineDOMtext+"Quantity").val())+parseInt(1)):$("#items").append("<div id='"+itemLineDOMtext+"'>"+itemLine+"</div> <input type='number' id='"+itemLineDOMtext+"Quantity' class='quantity' value='1' data-cost='"+cost+"'  data-shipping='"+shipping+"'' ></input> ")},addShippingTotal:function(e){this.totalShipping+=parseFloat(e);$("#shippingTotal").html(this.totalShipping.toFixed(2))},addItemsTotal:function(e){this.totalItems+=parseFloat(e);$("#itemsTotal").html(this.totalItems.toFixed(2))},addOrderTotal:function(){this.totalOrder=parseFloat(this.totalItems)+parseFloat(this.totalShipping);$("#orderTotal").html(this.totalOrder.toFixed(2))},setCartCookie:function(){$.cookie("LAM-Shop",JSON.stringify(),{expires:10,path:"/"})},getCartCookie:function(e){cookieShop=$.cookie("LAM-Shop");typeof cookieShop!="undefined"?jsonCookie=$.parseJSON(cookieShop):this.initialize(e)}};